<?php
require_once __DIR__.'/BaseController.php'; require_once __DIR__.'/../models/Admin.php'; class AuthController extends BaseController{ public function showLogin(){ if($_SERVER['REQUEST_METHOD']==='POST'){ $email=trim($_POST['email']??''); $pass=$_POST['password']??''; $m=new Admin($this->pdo); $u=$m->findByEmail($email); if($u && password_verify($pass,$u['password'])){ $_SESSION['admin_id']=$u['id']; $this->redirect('/dashboard'); } else { $this->view('auth/login.php',['error'=>'Invalid']); } } else { $this->view('auth/login.php'); } } public function logout(){ session_destroy(); $this->redirect('/login'); } }
